import { ElementRef, Injectable } from '@angular/core';
import { ActiveElement, ChartConfiguration, ChartOptions } from 'chart.js';
import { AnnotationOptions } from 'chartjs-plugin-annotation';

import { mergeDeepAll } from '../../../helpers/merge-deep';
import { ChartDataset, ChartHighlightedElements, ChartType, isNumberArray } from '../chart.types';
import { ChartConfigService } from '../configs/chart-config.service';

import { Chart } from './configured-chart-js';

@Injectable()
export class ChartJSService {
  private chart: Chart;

  constructor(private chartConfigService: ChartConfigService) {}

  public renderChart(args: {
    targetElement: ElementRef<HTMLCanvasElement>;
    type: ChartType;
    data: ChartDataset[] | number[];
    dataLabels?: string[] | string[][];
    customOptions?: ChartOptions;
    annotations?: AnnotationOptions[];
    highlightedElements?: ChartHighlightedElements;
  }): void {
    const {
      targetElement,
      type,
      data,
      dataLabels,
      customOptions,
      annotations,
      highlightedElements,
    } = args;

    const datasets = this.createDatasets(data, highlightedElements);
    const options = this.createOptionsObject({ type, customOptions, annotations });
    const config = this.createConfigurationObject(type, datasets, options, dataLabels);
    this.initializeNewChart(targetElement.nativeElement, config);
  }

  public redrawChart() {
    this.chart.update();
  }

  public updateData(data: ChartDataset[] | number[]): void {
    const datasets = this.createDatasets(data);
    this.chart.data.datasets = datasets;
  }

  public updateDataLabels(dataLabels: string[] | string[][]) {
    this.chart.data.labels = dataLabels;
  }

  public updateType(type: ChartType, customOptions?: ChartOptions) {
    if (type === 'bar' || type === 'column') {
      /* indexAxis does not update predictably; update by replacing 
         the chart entirely instead */
      this.destructivelyUpdateType(type, customOptions);
    } else {
      this.nonDestructivelyUpdateType(type, customOptions);
    }
  }

  public updateOptions(customOptions: ChartOptions, type: ChartType) {
    const annotations = this.getExistingChartAnnotations();
    this.chart.options = this.createOptionsObject({ type, customOptions, annotations });
  }

  public updateAnnotations(annotations: AnnotationOptions[]) {
    const annotationsWithDefaults = this.applyDefaultsToAnnotations(annotations);
    this.chart.options.plugins.annotation.annotations = annotationsWithDefaults;
  }

  public updateHighlightedElements(highlightedElements?: ChartHighlightedElements) {
    const oldDatasets = this.chart.data.datasets as ChartDataset[];

    // Clear old datasets of highlighted elements
    oldDatasets.map((dataset) => {
      if (dataset?.kirbyOptions?.highlightedElements) {
        delete dataset.kirbyOptions.highlightedElements;
      }
    });

    this.chart.data.datasets = this.createDatasets(oldDatasets, highlightedElements);
  }

  private getExistingChartAnnotations(): AnnotationOptions[] {
    const annotations = this.chart.options.plugins?.annotation?.annotations;
    /* In browser chart.js might return annotations as a Proxy object; force it to be an array.
       Each annotationOption in the resulting array  will also be a Proxy object. 
       But internally chart.js will just work with them as normal values */
    if (annotations !== undefined) {
      return Object.keys(annotations).map((key) => annotations[key]);
    } else {
      return [];
    }
  }

  private destructivelyUpdateType(type: ChartType, customOptions?: ChartOptions) {
    const datasets = this.chart.data.datasets as ChartDataset[];
    const dataLabels = this.chart.data.labels;
    const annotations = this.getExistingChartAnnotations();

    const options = this.createOptionsObject({ type, customOptions, annotations });
    const config = this.createConfigurationObject(type, datasets, options, dataLabels);
    const canvasElement = this.chart.canvas;

    this.chart.destroy();
    this.initializeNewChart(canvasElement, config);
  }

  private nonDestructivelyUpdateType(chartType: ChartType, customOptions?: ChartOptions) {
    const annotations = this.getExistingChartAnnotations();
    const options = this.createOptionsObject({
      type: chartType,
      customOptions,
      annotations,
    });

    this.chart.options = options;
    this.chart.config.type = this.chartConfigService.chartTypeToChartJSType(chartType);
  }

  private initializeNewChart(canvasElement: HTMLCanvasElement, config: ChartConfiguration) {
    this.chart = new Chart(canvasElement, config);
  }

  private createBlankLabels(datasets: ChartDataset[]): string[] {
    const largestDataset = datasets.reduce((previousDataset, currentDataset) =>
      previousDataset.data.length > currentDataset.data.length ? previousDataset : currentDataset
    );
    return Array(largestDataset.data.length).fill('');
  }

  private applyDefaultsToAnnotations(annotations: AnnotationOptions[]) {
    return annotations.map((annotation) => {
      const annotationTypeDefaults = this.chartConfigService.getAnnotationDefaults(annotation.type);
      return mergeDeepAll(annotationTypeDefaults, annotation);
    });
  }

  private createAnnotationPluginOptionsObject(annotations: AnnotationOptions[]) {
    const annotationsWithDefaults = this.applyDefaultsToAnnotations(annotations);
    return {
      plugins: {
        annotation: {
          annotations: annotationsWithDefaults,
        },
      },
    };
  }

  private applyInteractionFunctionsExtensions(options: ChartOptions): ChartOptions {
    const interactionFunctionsExtensions = this.chartConfigService.getInteractionFunctionsExtensions();
    Object.entries(interactionFunctionsExtensions).forEach(([key, _]) => {
      const callback = options[key];
      options[key] = (e: Event, a: ActiveElement[], c: Chart) => {
        interactionFunctionsExtensions[key](e, a, c, callback);
      };
    });
    return options;
  }

  private createOptionsObject(args: {
    type: ChartType;
    customOptions?: ChartOptions;
    annotations?: AnnotationOptions[];
  }): ChartOptions {
    const { type, customOptions, annotations } = args;

    const typeConfig = this.chartConfigService.getTypeConfig(type);
    const typeConfigOptions = typeConfig?.options;
    const annotationPluginOptions = annotations
      ? this.createAnnotationPluginOptionsObject(annotations)
      : {};
    let options: ChartOptions = mergeDeepAll(
      typeConfigOptions,
      customOptions,
      annotationPluginOptions
    );
    return this.applyInteractionFunctionsExtensions(options);
  }

  private createConfigurationObject(
    type: ChartType,
    datasets: ChartDataset[],
    options: ChartOptions,
    dataLabels?: unknown[]
  ): ChartConfiguration {
    /* chartJS requires labels; if none is provided create an empty string array
    to make it optional for consumer */
    const labels = !dataLabels ? this.createBlankLabels(datasets) : dataLabels;
    const typeConfig = this.chartConfigService.getTypeConfig(type);

    return mergeDeepAll(typeConfig, {
      data: {
        labels,
        datasets,
      },
      options,
    }) as ChartConfiguration;
  }

  private addHighlightedElementsToDatasets(
    highlightedElements: ChartHighlightedElements,
    datasets: ChartDataset[]
  ) {
    highlightedElements.forEach(([datasetIndex, dataIndex]) => {
      const dataset = datasets[datasetIndex];
      if (!dataset) return;

      if (dataset?.kirbyOptions?.highlightedElements) {
        dataset.kirbyOptions.highlightedElements.push(dataIndex);
      } else {
        dataset.kirbyOptions = {
          ...dataset.kirbyOptions,
          highlightedElements: [dataIndex],
        };
      }
    });
  }
  private createDatasets(
    data: ChartDataset[] | number[],
    highlightedElements?: ChartHighlightedElements
  ): ChartDataset[] {
    let datasets = isNumberArray(data) ? [{ data }] : data;

    if (highlightedElements) this.addHighlightedElementsToDatasets(highlightedElements, datasets);

    return datasets;
  }
}

import { Inject, Injectable, Optional } from '@angular/core';

import { Icon, IconSettings, ICON_SETTINGS } from './icon-settings';

@Injectable({
  providedIn: 'root',
})
export class IconRegistryService {
  private iconRegistry = new Map<string, string>();

  constructor(@Optional() @Inject(ICON_SETTINGS) iconSettings?: IconSettings) {
    if (iconSettings) {
      this.addIcons(iconSettings.icons);
      console.warn(`Use of IconSettings is deprecated, use IconRegistryService instead`);
    }
  }

  public addIcon(iconName: string, svgPath: string): void {
    if (!this.iconRegistry.has(iconName)) {
      this.iconRegistry.set(iconName, svgPath);
    } else {
      console.warn(`Icon with name: "${iconName}" already exists`);
    }
  }

  public addIcons(icons: Icon[]): void {
    if (!icons) {
      console.error('Icons not defined');
      return;
    }
    icons.forEach((icon) => {
      this.addIcon(icon.name, icon.svg);
    });
  }

  getIcons(): Icon[] {
    return [...this.iconRegistry].map(
      (keyValPair) => ({ name: keyValPair[0], svg: keyValPair[1] } as Icon)
    );
  }

  getIcon(name: string): Icon {
    const svg = this.iconRegistry.get(name);
    return svg ? { name, svg } : undefined;
  }
}

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class KeyboardHandlerService {
  handle(event: KeyboardEvent, items: any[] | string[], selectedIndex: number) {
    let newIndex = -1;
    switch (event.key) {
      case 'ArrowUp':
      case 'ArrowLeft':
        // Select previous item:
        newIndex = selectedIndex - 1;
        break;
      case 'ArrowDown':
      case 'ArrowRight':
        if (selectedIndex === undefined) {
          // None selected, select first item:
          newIndex = 0;
        } else if (selectedIndex < items.length - 1) {
          // Select next item:
          newIndex = selectedIndex + 1;
        }
        break;
      case 'Home':
        // Select first item:
        newIndex = 0;
        break;
      case 'End':
        // Select last item:
        newIndex = items.length - 1;
        break;
      default:
        break;
    }
    return newIndex;
  }
}

import { Injectable, Renderer2, RendererStyleFlags2 } from '@angular/core';

const config = Object.freeze({
  className: 'kirby-line-clamp',
  lineClamp: '--line-clamp',
  lineHeight: '--line-height',
});

@Injectable()
export class LineClampHelper {
  constructor(private renderer: Renderer2) {}

  setMaxLines(element: Element, maxLines: number): void {
    this.renderer.setStyle(element, config.lineClamp, maxLines, RendererStyleFlags2.DashCase);
    this.renderer.addClass(element, config.className);
  }

  setLineHeight(element: Element, lineHeight: string): void {
    // Only necessary for the fallback solution to work
    this.renderer.setStyle(element, config.lineHeight, lineHeight, RendererStyleFlags2.DashCase);
  }

  removeLineClamp(element: Element) {
    this.renderer.removeClass(element, config.className);
    this.renderer.removeStyle(element, config.lineClamp);
    this.renderer.removeStyle(element, config.lineHeight);
  }
}

import { Injectable } from '@angular/core';

import { ListComponent } from '../list.component';
import { LoadOnDemandEventData } from '../list.event';

@Injectable()
export class ListHelper {
  onLoadOnDemand(component: ListComponent, _event: LoadOnDemandEventData) {
    if (component.isLoadOnDemandEnabled && !component._isLoading) {
      component._isLoading = true;
      component.loadOnDemand.emit({
        complete: (disableLoadOnDemand: boolean) => {
          component.isLoadOnDemandEnabled = !disableLoadOnDemand;
          component._isLoading = false;
        },
      });
    }
  }
}

import { ComponentFactoryResolver, Injectable, Injector } from '@angular/core';
import { LoadingController } from '@ionic/angular';

import { SpinnerComponent } from '../spinner/spinner.component';

@Injectable({
  providedIn: 'root',
})
export class LoadingOverlayService {
  private ionLoading: HTMLIonLoadingElement = null;

  constructor(
    private loadingController: LoadingController,
    private componentFactoryResolver: ComponentFactoryResolver,
    private injector: Injector
  ) {}

  public async showLoadingOverlay(showBackdrop: boolean = true): Promise<void> {
    if (!this.ionLoading) {
      this.ionLoading = await this.loadingController.create({
        cssClass: 'kirby-loading-overlay',
        duration: 0,
        message: null,
        showBackdrop: showBackdrop,
        spinner: null,
      });

      const loadingWrapper = this.ionLoading.querySelector('.loading-wrapper');
      const kirbySpinner = document.createElement('kirby-spinner');
      const factory = this.componentFactoryResolver.resolveComponentFactory(SpinnerComponent);
      factory.create(this.injector, [], kirbySpinner);
      loadingWrapper.appendChild(kirbySpinner);

      await this.ionLoading.present();
    }
  }

  public async hideLoadingOverlay(): Promise<void> {
    if (!!this.ionLoading) {
      await this.ionLoading.dismiss();
      this.ionLoading = null;
    }
  }
}

import { Injectable } from '@angular/core';
import { Animation, AnimationBuilder, AnimationController } from '@ionic/angular';

import { KirbyAnimation } from '../../../animation/kirby-animation';
import { PlatformService } from '../../../helpers/platform.service';

@Injectable({ providedIn: 'root' })
export class ModalAnimationBuilderService {
  constructor(private animationCtrl: AnimationController, private platform: PlatformService) {}

  private readonly easingEnter = KirbyAnimation.Easing.modal.enter;
  private readonly easingLeave = KirbyAnimation.Easing.modal.exit;
  private readonly duration = KirbyAnimation.Duration.LONG;
  private readonly SwipeToCloseDefaults = {
    MIN_PRESENTING_SCALE: 0.93,
  };

  public enterAnimation(currentBackdrop?: HTMLIonBackdropElement): AnimationBuilder {
    return (baseEl: HTMLElement, presentingEl?: HTMLElement): Animation => {
      const backdropAnimation = this.animationCtrl
        .create()
        .addElement(baseEl.querySelector('ion-backdrop')!)
        .fromTo('opacity', 0.01, 'var(--backdrop-opacity)')
        .beforeStyles({
          'pointer-events': 'none',
        })
        .afterClearStyles(['pointer-events']);

      const wrapperAnimation = this.animationCtrl
        .create()
        .addElement(baseEl.querySelectorAll('.modal-wrapper, .modal-shadow')!)
        .beforeStyles({ opacity: 1 })
        .fromTo('transform', 'translateY(100vh)', 'translateY(0vh)');

      const baseAnimation = this.animationCtrl
        .create()
        .addElement(baseEl)
        .easing(this.easingEnter)
        .duration(this.duration)
        .addAnimation(wrapperAnimation);

      let currentBackdropAnimation: Animation;
      if (currentBackdrop) {
        currentBackdropAnimation = this.animationCtrl
          .create()
          .addElement(currentBackdrop)
          .fromTo('opacity', 'var(--backdrop-opacity)', 0.01);
      }

      if (presentingEl) {
        const isMobile = !this.platform.isPhabletOrBigger();
        const hasCardModal =
          presentingEl.tagName === 'ION-MODAL' &&
          (presentingEl as HTMLIonModalElement).presentingElement !== undefined;

        const presentingAnimation = this.animationCtrl.create().beforeStyles({
          transform: 'translateY(0)',
          'transform-origin': 'top center',
          overflow: 'hidden',
        });

        const bodyEl = document.body;

        if (isMobile) {
          /**
           * Fallback for browsers that does not support `max()` (ex: Firefox)
           * No need to worry about statusbar padding since engines like Gecko
           * are not used as the engine for standlone Cordova/Capacitor apps
           */
          const transformOffset = !CSS.supports('width', 'max(0px, 1px)')
            ? '30px'
            : 'max(30px, var(--ion-safe-area-top))';
          const modalTransform = hasCardModal ? '-10px' : transformOffset;
          const toPresentingScale = this.SwipeToCloseDefaults.MIN_PRESENTING_SCALE;
          const finalTransform = `translateY(${modalTransform}) scale(${toPresentingScale})`;

          presentingAnimation
            .afterStyles({
              transform: finalTransform,
            })
            .beforeAddWrite(() => bodyEl.style.setProperty('background-color', 'black'))
            .addElement(presentingEl)
            .keyframes([
              {
                offset: 0,
                filter: 'contrast(1)',
                transform: 'translateY(0px) scale(1)',
                borderRadius: '0px',
              },
              {
                offset: 1,
                filter: 'contrast(0.85)',
                transform: finalTransform,
                borderRadius: '10px 10px 0 0',
              },
            ]);
          baseAnimation.addAnimation(presentingAnimation);
        } else {
          baseAnimation.addAnimation(backdropAnimation);
          if (currentBackdropAnimation) {
            baseAnimation.addAnimation(currentBackdropAnimation);
          }

          if (!hasCardModal) {
            wrapperAnimation.fromTo('opacity', '0', '1');
          } else {
            const toPresentingScale = hasCardModal
              ? this.SwipeToCloseDefaults.MIN_PRESENTING_SCALE
              : 1;
            const finalTransform = `translateY(-10px) scale(${toPresentingScale})`;

            presentingAnimation
              .afterStyles({
                transform: finalTransform,
              })
              .addElement(presentingEl.querySelector('.modal-wrapper')!)
              .keyframes([
                { offset: 0, filter: 'contrast(1)', transform: 'translateY(0) scale(1)' },
                { offset: 1, filter: 'contrast(0.85)', transform: finalTransform },
              ]);

            const shadowAnimation = this.animationCtrl
              .create()
              .afterStyles({
                transform: finalTransform,
              })
              .addElement(presentingEl.querySelector('.modal-shadow')!)
              .keyframes([
                { offset: 0, opacity: '1', transform: 'translateY(0) scale(1)' },
                { offset: 1, opacity: '0', transform: finalTransform },
              ]);

            baseAnimation.addAnimation([presentingAnimation, shadowAnimation]);
          }
        }
      } else {
        baseAnimation.addAnimation(backdropAnimation);
        if (currentBackdropAnimation) {
          baseAnimation.addAnimation(currentBackdropAnimation);
        }
      }

      return baseAnimation;
    };
  }

  public leaveAnimation(currentBackdrop?: HTMLIonBackdropElement): AnimationBuilder {
    return (baseEl: HTMLElement, presentingEl?: HTMLElement): Animation => {
      const backdropAnimation = this.animationCtrl
        .create()
        .addElement(baseEl.querySelector('ion-backdrop')!)
        .fromTo('opacity', 'var(--backdrop-opacity)', 0.0);

      const wrapperAnimation = this.animationCtrl
        .create()
        .addElement(baseEl.querySelectorAll('.modal-wrapper, .modal-shadow')!)
        .beforeStyles({ opacity: 1 })
        .fromTo('transform', 'translateY(0vh)', 'translateY(100vh)');

      const baseAnimation = this.animationCtrl
        .create()
        .addElement(baseEl)
        .easing(this.easingLeave)
        .duration(this.duration)
        .addAnimation(wrapperAnimation);

      let currentBackdropAnimation: Animation;
      if (currentBackdrop) {
        currentBackdropAnimation = this.animationCtrl
          .create()
          .addElement(currentBackdrop)
          .fromTo('opacity', 0.01, 'var(--backdrop-opacity)')
          .afterStyles({ opacity: 'var(--backdrop-opacity)' }); //Ensures backdrop is reset to default opacity after swipe to close
      }

      if (presentingEl) {
        const isMobile = !this.platform.isPhabletOrBigger();
        const hasCardModal =
          presentingEl.tagName === 'ION-MODAL' &&
          (presentingEl as HTMLIonModalElement).presentingElement !== undefined;

        const presentingAnimation = this.animationCtrl
          .create()
          .beforeClearStyles(['transform'])
          .afterClearStyles(['transform'])
          .onFinish((currentStep) => {
            // only reset background color if this is the last card-style modal
            if (currentStep !== 1) {
              return;
            }

            presentingEl.style.setProperty('overflow', '');

            const numModals = Array.from(bodyEl.querySelectorAll('ion-modal')).filter(
              (m) => m.presentingElement !== undefined
            ).length;
            if (numModals <= 1) {
              bodyEl.style.setProperty('background-color', '');
            }
          });

        const bodyEl = document.body;

        if (isMobile) {
          const transformOffset = !CSS.supports('width', 'max(0px, 1px)')
            ? '30px'
            : 'max(30px, var(--ion-safe-area-top))';
          const modalTransform = hasCardModal ? '-10px' : transformOffset;
          const toPresentingScale = this.SwipeToCloseDefaults.MIN_PRESENTING_SCALE;
          const finalTransform = `translateY(${modalTransform}) scale(${toPresentingScale})`;

          presentingAnimation.addElement(presentingEl).keyframes([
            {
              offset: 0,
              filter: 'contrast(0.85)',
              transform: finalTransform,
              borderRadius: '10px 10px 0 0',
            },
            {
              offset: 1,
              filter: 'contrast(1)',
              transform: 'translateY(0px) scale(1)',
              borderRadius: '0px',
            },
          ]);

          baseAnimation.addAnimation(presentingAnimation);
        } else {
          baseAnimation.addAnimation(backdropAnimation);
          if (currentBackdropAnimation) {
            baseAnimation.addAnimation(currentBackdropAnimation);
          }

          if (!hasCardModal) {
            wrapperAnimation.fromTo('opacity', '1', '0');
          } else {
            const toPresentingScale = hasCardModal
              ? this.SwipeToCloseDefaults.MIN_PRESENTING_SCALE
              : 1;
            const finalTransform = `translateY(-10px) scale(${toPresentingScale})`;

            presentingAnimation
              .addElement(presentingEl.querySelector('.modal-wrapper')!)
              .afterStyles({
                transform: 'translate3d(0, 0, 0)',
              })
              .keyframes([
                { offset: 0, filter: 'contrast(0.85)', transform: finalTransform },
                { offset: 1, filter: 'contrast(1)', transform: 'translateY(0) scale(1)' },
              ]);

            const shadowAnimation = this.animationCtrl
              .create()
              .addElement(presentingEl.querySelector('.modal-shadow')!)
              .afterStyles({
                transform: 'translateY(0) scale(1)',
              })
              .keyframes([
                { offset: 0, opacity: '0', transform: finalTransform },
                { offset: 1, opacity: '1', transform: 'translateY(0) scale(1)' },
              ]);

            baseAnimation.addAnimation([presentingAnimation, shadowAnimation]);
          }
        }
      } else {
        baseAnimation.addAnimation(backdropAnimation);
        if (currentBackdropAnimation) {
          baseAnimation.addAnimation(currentBackdropAnimation);
        }
      }

      return baseAnimation;
    };
  }
}

import { Inject, Injectable, OnDestroy, Optional } from '@angular/core';
import { ActivatedRoute, Params, Routes, ROUTES } from '@angular/router';
import { Observable, Subject } from 'rxjs';
import { filter, map, takeUntil } from 'rxjs/operators';

import { KirbyAnimation } from '../../../animation/kirby-animation';
import { ActionSheetConfig } from '../action-sheet/config/action-sheet-config';
import { AlertConfig } from '../alert/config/alert-config';
import { ModalConfig } from '../modal-wrapper/config/modal-config';

import { ActionSheetHelper } from './action-sheet.helper';
import { AlertHelper } from './alert.helper';
import { ModalNavigationService } from './modal-navigation.service';
import { ModalHelper } from './modal.helper';
import { ModalRouteActivation, Overlay } from './modal.interfaces';

@Injectable()
export class ModalController implements OnDestroy {
  private overlays: Overlay[] = [];
  private readonly noOverlayRegisteredErrorMessage = 'No modal overlays are currently registered';
  private destroy$ = new Subject<void>();

  constructor(
    private modalHelper: ModalHelper,
    private actionSheetHelper: ActionSheetHelper,
    private alertHelper: AlertHelper,
    private modalNavigationService: ModalNavigationService,
    @Optional() @Inject(ROUTES) private routeConfig: Routes[]
  ) {}

  async initialize(moduleRootRoutePath?: string) {
    const modalNavigation = await this.modalNavigationService.getModalNavigation(
      this.routeConfig,
      moduleRootRoutePath
    );
    this.onModalRouteActivated(modalNavigation.activated$);
    this.onModalRouteDeactivated(modalNavigation.deactivated$); // TODO: Do we want to close modal when routing out of modal route? Or should the code that navigates close the window??
  }

  private onModalRouteActivated(modalRouteActivated$: Observable<ModalRouteActivation>) {
    const navigateOnWillClose = () => {
      this.modalNavigationService.navigateOutOfModalOutlet();
    };
    const siblingModalRouteActivated$ = modalRouteActivated$.pipe(
      filter((modalRouteActivation) => !modalRouteActivation.isNewModal),
      map((modalRouteActivation) => modalRouteActivation.route)
    );

    modalRouteActivated$
      .pipe(
        takeUntil(this.destroy$),
        filter(() => this.overlays.length === 0)
      )
      .subscribe(async (modalRouteActivation) => {
        if (modalRouteActivation.isNewModal) {
          await this.showModalRoute(
            modalRouteActivation.route,
            siblingModalRouteActivated$,
            navigateOnWillClose
          );
        }
      });
  }

  private onModalRouteDeactivated(modalRouteDeactivated$: Observable<boolean>) {
    modalRouteDeactivated$
      .pipe(
        takeUntil(this.destroy$),
        filter(() => this.overlays.length > 0) // TODO: This also fires when closing overlay - should we check for isClosing??
      )
      .subscribe(async () => {
        await this.hideAll();
      });
  }

  public async showModal(config: ModalConfig, onClose?: (data?: any) => void): Promise<void> {
    if (config.hasOwnProperty('dim')) {
      console.warn('ModalConfig.dim is deprecated - please remove from your configuration.');
    }
    await this.showAndRegisterOverlay(() => this.modalHelper.showModalWindow(config), onClose);
  }

  public async navigateToModal(path: string | string[], queryParams?: Params): Promise<boolean> {
    return this.modalNavigationService.navigateToModal(path, queryParams);
  }

  public async navigateWithinModal(relativePath: string, queryParams?: Params): Promise<boolean> {
    return this.modalNavigationService.navigateWithinModal(relativePath, queryParams);
  }

  private async showModalRoute(
    modalRoute: ActivatedRoute,
    siblingModalRouteActivated$: Observable<ActivatedRoute>,
    onWillClose: (data?: any) => void
  ): Promise<void> {
    const config: ModalConfig = {
      component: null,
      modalRoute: modalRoute,
      siblingModalRouteActivated$: siblingModalRouteActivated$,
      flavor: 'modal', // Todo: Should it be possible to specify flavor as data in RouteConfig?
    };
    await this.showAndRegisterOverlay(
      () => this.modalHelper.showModalWindow(config),
      null,
      onWillClose
    );
  }

  public async showActionSheet(
    config: ActionSheetConfig,
    onClose?: (data?: any) => void
  ): Promise<void> {
    await this.showAndRegisterOverlay(
      () => this.actionSheetHelper.showActionSheet(config),
      onClose
    );
  }

  public async showAlert(config: AlertConfig, onClose?: (result: boolean) => void): Promise<void> {
    await this.showAndRegisterOverlay(() => this.alertHelper.showAlert(config), onClose);
  }

  private async showAndRegisterOverlay(
    showOverlay: () => Promise<Overlay>,
    onCloseOverlay?: (data?: any) => void,
    onWillCloseOverlay?: (data?: any) => void
  ) {
    const overlay = await showOverlay();
    if (!overlay) return;

    this.overlays.push(overlay);

    if (typeof onWillCloseOverlay === 'function') {
      overlay.onWillDismiss.then((event) => onWillCloseOverlay(event.data));
    }

    overlay.onDidDismiss.then((event) => {
      this.overlays.pop();
      if (typeof onCloseOverlay === 'function') {
        onCloseOverlay(event.data);
      }
    });
  }

  public registerPresentingElement(element: HTMLElement) {
    this.modalHelper.registerPresentingElement(element);
  }

  public async hideTopmost(data?: any): Promise<boolean> {
    const overlay = this.overlays[this.overlays.length - 1];
    if (!overlay) {
      throw new Error(this.noOverlayRegisteredErrorMessage);
    }
    return overlay.dismiss(data);
  }

  /**
   * @deprecated Will be removed in next major version. Inject Modal in embedded component and use Modal.scrollToTop instead.
   */
  public scrollToTop(duration?: KirbyAnimation.Duration) {
    console.warn(
      'ModalController.scrollToTop is deprecated - please inject Modal in embedded component and use Modal.scrollToTop instead.'
    );
    const overlay = this.overlays[this.overlays.length - 1];
    if (!overlay) {
      throw new Error(this.noOverlayRegisteredErrorMessage);
    }
    this.modalHelper.scrollToTop(this.noOverlayRegisteredErrorMessage, duration);
  }

  /**
   * @deprecated Will be removed in next major version. Inject Modal in embedded component and use Modal.scrollToBottom instead.
   */
  public scrollToBottom(duration?: KirbyAnimation.Duration) {
    console.warn(
      'ModalController.scrollToBottom is deprecated - please inject Modal in embedded component and use Modal.scrollToBottom instead.'
    );
    const overlay = this.overlays[this.overlays.length - 1];
    if (!overlay) {
      throw new Error(this.noOverlayRegisteredErrorMessage);
    }
    this.modalHelper.scrollToBottom(this.noOverlayRegisteredErrorMessage, duration);
  }

  public async hideAll(): Promise<void> {
    await Promise.all(
      this.overlays.map(async (overlay) => {
        await overlay.dismiss();
      })
    );
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}

import { Injectable } from '@angular/core';
import { ModalController } from '@ionic/angular';

import { KirbyAnimation } from '../../../animation/kirby-animation';
import { WindowRef } from '../../../types/window-ref';
import { ModalCompactWrapperComponent } from '../modal-wrapper/compact/modal-compact-wrapper.component';
import { ModalConfig, ModalFlavor, ModalSize } from '../modal-wrapper/config/modal-config';
import { ModalWrapperComponent } from '../modal-wrapper/modal-wrapper.component';

import { ModalAnimationBuilderService } from './modal-animation-builder.service';
import { Overlay } from './modal.interfaces';

@Injectable()
export class ModalHelper {
  // TODO: Make presentingElement an instance field when
  // forRoot()/singleton services has been solved:
  private static presentingElement: HTMLElement = undefined;

  constructor(
    private ionicModalController: ModalController,
    private modalAnimationBuilder: ModalAnimationBuilderService,
    private windowRef: WindowRef
  ) {}

  public async showModalWindow(config: ModalConfig): Promise<Overlay> {
    config.flavor = config.flavor || 'modal';
    const modalPresentingElement = await this.getPresentingElement(config.flavor);

    let currentBackdrop: HTMLIonBackdropElement;
    const topMostModal = await this.ionicModalController.getTop();
    if (topMostModal) {
      currentBackdrop = topMostModal.querySelector<HTMLIonBackdropElement>('ion-backdrop');
    }

    const enterAnimation = this.modalAnimationBuilder.enterAnimation(currentBackdrop);
    const leaveAnimation = this.modalAnimationBuilder.leaveAnimation(currentBackdrop);

    const defaultModalSize: ModalSize = config.flavor === 'modal' ? 'medium' : null;
    const modalSize = config.size || defaultModalSize;
    const allow_scroll_class = 'allow-background-scroll';

    let customCssClasses = [];
    if (config.cssClass) {
      customCssClasses = Array.isArray(config.cssClass) ? config.cssClass : [config.cssClass];
    }

    if (config.interactWithBackground) {
      this.windowRef.nativeWindow.document.body.classList.add(allow_scroll_class);
    }

    const ionModal = await this.ionicModalController.create({
      component: config.flavor === 'compact' ? ModalCompactWrapperComponent : ModalWrapperComponent,
      cssClass: [
        'kirby-overlay',
        'kirby-modal',
        config.flavor === 'drawer' ? 'kirby-drawer' : null,
        config.flavor === 'compact' ? 'kirby-modal-compact' : null,
        'kirby-modal-' + modalSize,
        config.interactWithBackground ? 'interact-with-background' : null,
        ...customCssClasses,
      ],
      backdropDismiss: config.flavor === 'compact' || config.interactWithBackground ? false : true,
      showBackdrop: !config.interactWithBackground,
      componentProps: { config: config },
      swipeToClose: config.flavor != 'compact',
      presentingElement: modalPresentingElement,
      keyboardClose: false,
      enterAnimation,
      leaveAnimation,
    });

    if (config.interactWithBackground) {
      ionModal.onDidDismiss().then(() => {
        this.windowRef.nativeWindow.document.body.classList.remove(allow_scroll_class);
      });
    }

    await ionModal.present();

    return {
      dismiss: ionModal.dismiss.bind(ionModal),
      onWillDismiss: ionModal.onWillDismiss(),
      onDidDismiss: ionModal.onDidDismiss(),
    };
  }

  public registerPresentingElement(element: HTMLElement) {
    ModalHelper.presentingElement = element;
  }

  private async getPresentingElement(flavor?: ModalFlavor) {
    let modalPresentingElement: HTMLElement = undefined;
    if (!flavor || flavor === 'modal') {
      const topMostModal = await this.ionicModalController.getTop();
      if (!topMostModal) {
        modalPresentingElement = ModalHelper.presentingElement;
      } else if (
        !topMostModal.classList.contains('kirby-drawer') &&
        !topMostModal.classList.contains('kirby-modal-compact')
      ) {
        modalPresentingElement = topMostModal;
      }
    }
    return modalPresentingElement;
  }

  public async scrollToTop(
    noModalRegisteredErrorMessage: string,
    duration?: KirbyAnimation.Duration
  ) {
    const modal = await this.ionicModalController.getTop();
    if (!modal || !(modal.component instanceof ModalWrapperComponent)) {
      throw new Error(noModalRegisteredErrorMessage);
    }
    modal.component.scrollToTop(duration);
  }

  public async scrollToBottom(
    noModalRegisteredErrorMessage: string,
    duration?: KirbyAnimation.Duration
  ) {
    const modal = await this.ionicModalController.getTop();
    if (!modal || !(modal.component instanceof ModalWrapperComponent)) {
      throw new Error(noModalRegisteredErrorMessage);
    }
    modal.component.scrollToBottom(duration);
  }
}

