import {
  Directive,
  Input,
  ComponentFactoryResolver,
  OnInit,
  Renderer2,
  ViewContainerRef,
} from '@angular/core';

import { ComponentConfiguration } from './component-configuration';
import { DynamicComponent } from './dynamic-component';

@Directive({
  selector: '[kirbyLoadComponent]',
})
export class ComponentLoaderDirective implements OnInit {
  // tslint:disable-next-line:no-input-rename
  @Input('kirbyLoadComponent') configuration: ComponentConfiguration;
  @Input() cssClass: string;

  constructor(
    private componentFactoryResolver: ComponentFactoryResolver,
    private viewContainerRef: ViewContainerRef,
    private renderer: Renderer2
  ) {}

  ngOnInit() {
    this.loadCard();
  }

  loadCard() {
    const componentFactory = this.componentFactoryResolver.resolveComponentFactory(
      this.configuration.component
    );
    this.viewContainerRef.clear();
    const componentRef = this.viewContainerRef.createComponent(componentFactory);
    if (componentRef && componentRef.location && componentRef.location.nativeElement) {
      this.renderer.addClass(componentRef.location.nativeElement, this.cssClass);
    }
    (<DynamicComponent>componentRef.instance).data = this.configuration.data;
  }
}

import { FormatWidth, getLocaleDateFormat } from '@angular/common';
import {
  AfterViewInit,
  Directive,
  ElementRef,
  HostListener,
  Inject,
  LOCALE_ID,
  Renderer2,
} from '@angular/core';
import 'inputmask/lib/extensions/inputmask.date.extensions';
import Inputmask from 'inputmask/lib/inputmask';

@Directive({
  selector: '[kirby-input][type="date"]',
})
export class DateInputDirective implements AfterViewInit {
  @HostListener('input')
  onInput() {
    this.updateMask(this.elementRef.nativeElement.value);
  }

  private maskingElement: HTMLElement;

  constructor(
    private elementRef: ElementRef,
    private renderer: Renderer2,
    @Inject(LOCALE_ID) private locale: string
  ) {
    // Remove type to avoid user-agent specific behaviour for [type="date"]
    this.elementRef.nativeElement.removeAttribute('type');
  }

  ngAfterViewInit(): void {
    this.initMask();
  }

  private initMask(): void {
    const inputFormat = this.getInputFormat();
    const placeholder = this.getPlaceholder(inputFormat);

    // Set initial placeholder ex. dd/mm/yyyy
    this.renderer.setAttribute(this.elementRef.nativeElement, 'placeholder', placeholder);

    // Init InputMask
    new Inputmask('datetime', {
      inputFormat,
      placeholder,
    }).mask(this.elementRef.nativeElement);

    // Append input overlay, so it's possible to style typed date differntly than the date-mask
    this.appendMaskingElement();
  }

  // Keeps order and seperator from speficied locale
  private getInputFormat(): string {
    const localeDateFormat = getLocaleDateFormat(this.locale, FormatWidth.Short);
    return localeDateFormat
      .toLowerCase()
      .replace(/d+/, 'dd')
      .replace(/m+/, 'mm')
      .replace(/y+/, 'yyyy');
  }

  private getPlaceholder(inputFormat: string): string {
    return this.locale === 'da' ? inputFormat.split('y').join('') : inputFormat;
  }

  private appendMaskingElement(): void {
    const wrapper = this.wrapElement(this.elementRef.nativeElement);
    this.renderer.addClass(wrapper, 'date-mask-wrapper');

    this.maskingElement = this.renderer.createElement('div');
    this.renderer.appendChild(wrapper, this.maskingElement);

    this.renderer.addClass(this.maskingElement, 'date-mask');
  }

  private wrapElement(element: HTMLElement): HTMLElement {
    const wrapper = this.renderer.createElement('div');
    const parent = element.parentElement;
    this.renderer.insertBefore(parent, wrapper, element);
    this.renderer.appendChild(wrapper, element);
    return wrapper;
  }

  private updateMask(value: string): void {
    if (!this.maskingElement) return;
    const lastNumber = value.match(/.*?(\d)[^\d]*$/); // get last number in string
    this.maskingElement.innerHTML = value
      ? value.slice(0, value.lastIndexOf(lastNumber[1]) + 1)
      : '';
  }
}

import { getLocaleNumberSymbol, NumberSymbol } from '@angular/common';
import { Directive, ElementRef, Inject, Input, LOCALE_ID, OnInit } from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import 'inputmask/lib/extensions/inputmask.numeric.extensions';
import Inputmask from 'inputmask/lib/inputmask';

interface InputMask {
  unmaskedvalue: () => string;
  setValue: (val: string) => void;
}

@Directive({
  // tslint:disable-next-line
  selector: '[kirby-decimal-mask]',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      multi: true,
      useExisting: DecimalMaskDirective,
    },
  ],
})
export class DecimalMaskDirective implements ControlValueAccessor, OnInit {
  @Input() min: number;
  @Input() max: number;
  @Input() precision = 2;
  @Input() setMaxOnOverflow = false;

  @Input() set allowMinus(allowMinus: boolean) {
    this._allowMinus = allowMinus || (this.min || 0) < 0;
  }

  @Input() set disableGroupSeperator(disabled: string) {
    this._groupSeperatorDisabled = String(disabled) === '' || String(disabled) === 'true';
  }

  @Input() set maxlength(maxlength: number) {
    this._maxlength = maxlength;

    const maxlengthValue = parseInt('9'.repeat(maxlength));
    this.max = this.getMax(maxlengthValue);
    this.min = this.getMin(maxlengthValue);
  }

  groupSeparator = getLocaleNumberSymbol(this.locale, NumberSymbol.Group);
  radixPoint = getLocaleNumberSymbol(this.locale, NumberSymbol.Decimal);
  inputmask: InputMask;

  _allowMinus = false;
  _maxlength: number;
  _groupSeperatorDisabled: boolean;

  onChange = (_: string) => {};

  constructor(private elementRef: ElementRef, @Inject(LOCALE_ID) private locale: string) {}

  ngOnInit(): void {
    // Set type="text", because functionality like 'setSelectionRange' are not supported on type="number"
    this.elementRef.nativeElement.setAttribute('type', 'text');

    // Remove maxlength as this is handled by the mask
    this.elementRef.nativeElement.removeAttribute('maxlength');

    this.initMask();
  }

  writeValue(val: number): void {
    if (!this.inputmask) return;
    const formattedValue = String(val).replace('.', this.radixPoint);
    this.inputmask.setValue(formattedValue);
  }

  registerOnChange(onChange: any): void {
    this.onChange = onChange;
  }

  registerOnTouched(_: any): void {}

  private initMask(): void {
    new Inputmask('decimal', {
      groupSeparator: this._groupSeperatorDisabled ? '' : this.groupSeparator,
      radixPoint: this.radixPoint,
      digits: this._maxlength ? 0 : this.precision,
      min: this.min,
      max: this.max,
      allowMinus: this._allowMinus,
      negationSymbol: {
        front: getLocaleNumberSymbol(this.locale, NumberSymbol.MinusSign),
        back: '',
      },
      SetMaxOnOverflow: this.setMaxOnOverflow,
      showMaskOnFocus: false,
      showMaskOnHover: false,
      placeholder: '',
      onBeforeWrite: () => {
        if (!this.inputmask) return;
        const unmaskedValue = this.inputmask.unmaskedvalue();
        this.onChange(unmaskedValue.replace(this.radixPoint, '.'));
      },
    }).mask(this.elementRef.nativeElement);
    this.inputmask = this.elementRef.nativeElement.inputmask;
  }

  private getMax(maxlengthValue: number): number {
    return this.max === undefined ? maxlengthValue : Math.min(this.max, maxlengthValue);
  }

  private getMin(maxlengthValue: number): number {
    if (!this.allowMinus) return;
    maxlengthValue = -Math.abs(maxlengthValue);
    return this.min === undefined ? maxlengthValue : -Math.abs(Math.max(this.min, maxlengthValue));
  }
}

