@Component({
  selector: 'kirby-page',
  templateUrl: './page.component.html',
  styleUrls: ['./page.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class PageComponent
  implements OnInit, OnDestroy, AfterViewInit, AfterContentChecked, OnChanges {
  @Input() title: string;
  @Input() toolbarTitle: string;
  @Input() titleAlignment: 'left' | 'center' | 'right' = 'left';
  @Input() defaultBackHref: string;
  @Input() hideBackButton: boolean;
  @Input() titleMaxLines: number;

  private _tabBarBottomHidden: boolean;
  public get tabBarBottomHidden(): boolean {
    return this._tabBarBottomHidden;
  }
  @Input()
  public set tabBarBottomHidden(value: boolean) {
    if (this.tabsComponent) {
      // as we are setting a class on tabs, we need this to happen in a separate cd cycle
      setTimeout(() => (this.tabsComponent.tabBarBottomHidden = value));
    }
    this._tabBarBottomHidden = value;
  }

  @Output() enter = new EventEmitter<void>();
  @Output() leave = new EventEmitter<void>();
  @Output() refresh = new EventEmitter<PullToRefreshEvent>();

  @ViewChild(IonContent, { static: true }) private content: IonContent;
  @ViewChild(IonContent, { static: true, read: ElementRef })
  private ionContentElement: ElementRef<HTMLIonContentElement>;
  @ViewChild(IonHeader, { static: true, read: ElementRef })
  ionHeaderElement: ElementRef<HTMLIonHeaderElement>;
  @ViewChild(IonFooter, { static: true, read: ElementRef })
  private ionFooterElement: ElementRef<HTMLIonFooterElement>;

  @ViewChild('pageTitle', { static: false, read: ElementRef })
  private pageTitle: ElementRef;

  @ViewChild('simpleTitleTemplate', { static: true, read: TemplateRef })
  private simpleTitleTemplate: TemplateRef<any>;
  @ViewChild('simpleToolbarTitleTemplate', { static: true, read: TemplateRef })
  private simpleToolbarTitleTemplate: TemplateRef<any>;
  @ContentChild(PageToolbarTitleDirective, { static: false, read: TemplateRef })
  private customToolbarTitleTemplate: TemplateRef<any>;
  @ContentChild(PageTitleDirective, { static: false, read: TemplateRef })
  customTitleTemplate: TemplateRef<any>;
  @ContentChildren(PageActionsDirective)
  customActions: QueryList<PageActionsDirective>;
  @ContentChildren(PageContentDirective)
  private customContent: QueryList<PageContentDirective>;

  hasPageTitle: boolean;
  hasActionsInPage: boolean;
  toolbarTitleVisible: boolean;
  toolbarFixedActionsVisible: boolean;
  toolbarStickyActionsVisible: boolean;

  fitHeadingConfig: FitHeadingConfig;

  toolbarTitleTemplate: TemplateRef<any>;
  customContentTemplate: TemplateRef<any>;
  pageActionsTemplate: TemplateRef<any>;
  fixedContentTemplate: TemplateRef<any>;
  stickyActionsTemplate: TemplateRef<any>;
  fixedActionsTemplate: TemplateRef<any>;
  private pageTitleIntersectionObserverRef: IntersectionObserver = this.pageTitleIntersectionObserver();
  private urls: string[] = [];
  private hasEntered: boolean;

  private ngOnDestroy$ = new Subject();
  private navigationStart$: Observable<RouterEvent> = this.router.events.pipe(
    takeUntil(this.ngOnDestroy$),
    filter((event: RouterEvent) => event instanceof NavigationStart)
  );

  private navigationEnd$: Observable<RouterEvent> = this.router.events.pipe(
    takeUntil(this.ngOnDestroy$),
    filter((event: RouterEvent) => event instanceof NavigationEnd)
  );

  constructor(
    private elementRef: ElementRef,
    private renderer: Renderer2,
    private router: Router,
    private changeDetectorRef: ChangeDetectorRef,
    private windowRef: WindowRef,
    private modalNavigationService: ModalNavigationService,
    @Optional() @SkipSelf() private tabsComponent: TabsComponent
  ) {}

  ngOnInit(): void {
    this.removeWrapper();
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes.titleMaxLines) {
      this.fitHeadingConfig = {
        ...this.fitHeadingConfig,
        maxLines: changes.titleMaxLines.currentValue,
      };
    }
  }

  ngAfterViewInit(): void {
    this.navigationStart$.subscribe((event: NavigationStart) => {
      if (
        !this.urls.includes(event.url) &&
        !this.modalNavigationService.isModalRoute(event.url) &&
        !this.modalNavigationService.isModalRoute(this.router.url)
      ) {
        this.onLeave();
      }
    });

    this.navigationEnd$.subscribe((event: NavigationEnd) => {
      if (this.urls.includes(event.urlAfterRedirects)) {
        this.onEnter();
      }
    });

    this.windowRef.nativeWindow.addEventListener(selectedTabClickEvent, () => {
      this.content.scrollToTop(KirbyAnimation.Duration.LONG);
    });
  }

  ngAfterContentChecked(): void {
    if (!this.urls.includes(this.router.url)) {
      this.urls.push(this.router.url);
      this.onEnter();
    }

    this.initializeTitle();
    this.initializeActions();
    this.initializeContent();
    this.changeDetectorRef.detectChanges();
  }

  ngOnDestroy(): void {
    this.ngOnDestroy$.next();
    this.ngOnDestroy$.complete();

    this.pageTitleIntersectionObserverRef.disconnect();
    this.windowRef.nativeWindow.removeEventListener(selectedTabClickEvent, () => {
      this.content.scrollToTop(KirbyAnimation.Duration.LONG);
    });
  }

  delegateRefreshEvent(event: any): void {
    this.refresh.emit({
      complete: event.target.complete.bind(event.target),
    });
  }

  private onEnter() {
    if (this.hasEntered) return;
    this.hasEntered = true;

    this.enter.emit();
    if (this.pageTitle) {
      this.pageTitleIntersectionObserverRef.observe(this.pageTitle.nativeElement);
    }
  }

  private onLeave() {
    this.leave.emit();
    if (this.pageTitle) {
      this.pageTitleIntersectionObserverRef.unobserve(this.pageTitle.nativeElement);
    }
    this.hasEntered = false;

    if (this.tabBarBottomHidden && this.tabsComponent) {
      this.tabsComponent.tabBarBottomHidden = false;
    }
  }

  private initializeTitle() {
    // Ensures initializeTitle() won't run, if already initialized
    if (this.hasPageTitle) return;

    this.hasPageTitle = this.title !== undefined || !!this.customTitleTemplate;
    this.toolbarTitleVisible = !this.hasPageTitle;

    if (this.hasPageTitle) {
      setTimeout(() => {
        this.pageTitleIntersectionObserverRef.observe(this.pageTitle.nativeElement);
      });
    }

    const defaultTitleTemplate = this.customTitleTemplate || this.simpleTitleTemplate;
    // tslint:disable:prettier
    // prettier-ignore
    this.toolbarTitleTemplate = this.customToolbarTitleTemplate
      ? this.customToolbarTitleTemplate
      : typeof this.toolbarTitle === 'string'
        ? this.simpleToolbarTitleTemplate
        : defaultTitleTemplate;
  }

  private initializeActions() {
    this.customActions.forEach((pageAction) => {
      if (pageAction.isFixed) {
        this.fixedActionsTemplate = pageAction.template;
        this.toolbarFixedActionsVisible = true;
      } else {
        this.pageActionsTemplate = pageAction.template;
        if (pageAction.isSticky) {
          this.stickyActionsTemplate = pageAction.template;
        }
      }
    });
    this.hasActionsInPage = !!this.pageActionsTemplate;
  }

  private initializeContent() {
    this.customContent.forEach((content) => {
      if (content.isFixed) {
        this.fixedContentTemplate = content.template;
      } else {
        this.customContentTemplate = content.template;
      }
    });
  }

  private removeWrapper() {
    const parent = this.elementRef.nativeElement.parentNode;
    this.renderer.removeChild(parent, this.elementRef.nativeElement);
    this.renderer.appendChild(parent, this.ionHeaderElement.nativeElement);
    this.renderer.appendChild(parent, this.ionContentElement.nativeElement);
    this.renderer.appendChild(parent, this.ionFooterElement.nativeElement);
  }

  private pageTitleIntersectionObserver() {
    const options = {
      rootMargin: '0px',
    };

    let initialized = false;
    const callback = (entries) => {
      if (initialized) {
        this.toolbarTitleVisible = !entries[0].isIntersecting;
        this.toolbarStickyActionsVisible = !entries[0].isIntersecting;
        this.changeDetectorRef.detectChanges();
      } else {
        initialized = true;
      }
    };
    return new IntersectionObserver(callback, options);
  }

  @HostListener('window:keyboardWillShow', ['$event'])
  _onKeyboardWillShow(info?: { keyboardHeight: number }) {
    if (info && info.keyboardHeight) {
      this.ionContentElement.nativeElement.style.setProperty(
        '--keyboard-offset',
        `${info.keyboardHeight}px`
      );
    }
  }

  @HostListener('window:keyboardWillHide')
  _onKeyboardWillHide() {
    this.ionContentElement.nativeElement.style.setProperty('--keyboard-offset', '0px');
  }
}

import {
  AfterViewInit,
  Component,
  ElementRef,
  EventEmitter,
  HostListener,
  Input,
  OnDestroy,
  Output,
  Renderer2,
  ViewChild,
} from '@angular/core';

import { DesignTokenHelper } from '../../helpers';

export enum HorizontalDirection {
  right = 'right',
  left = 'left',
}

@Component({
  selector: 'kirby-popover',
  template: `
    <div #wrapper class="wrapper"><ng-content></ng-content></div>
  `,
  styleUrls: ['./popover.component.scss'],
})
export class PopoverComponent implements AfterViewInit, OnDestroy {
  // removeEventListener same instance of event handler & options
  private readonly preventScrollEventListenerOptions = {
    passive: false,
  } as EventListenerOptions;

  private readonly POPOVER_BODY_PADDING = 12;

  private isShowing: boolean = false;
  private isFirstToLockScroll: boolean;
  private zIndex: number;
  private document: Document;

  @ViewChild('wrapper', { static: true, read: ElementRef })
  wrapperElement: ElementRef<HTMLDivElement>;

  @Input()
  popout: HorizontalDirection = HorizontalDirection.right;

  @Input()
  target: HTMLElement | ElementRef<HTMLElement>;

  @Output()
  willHide = new EventEmitter<void>();

  private get targetElement(): HTMLElement {
    if (this.target instanceof ElementRef) {
      return this.target.nativeElement;
    } else {
      return this.target;
    }
  }

  @HostListener('click')
  _backdropClick() {
    this.willHide.emit();
    this.hide();
  }

  @HostListener('window:resize')
  _onWindowResize() {
    if (this.isShowing) {
      this.willHide.emit();
      this.hide();
    }
  }

  constructor(private elementRef: ElementRef<HTMLElement>, private renderer: Renderer2) {
    this.document = elementRef.nativeElement.ownerDocument;
  }

  ngAfterViewInit(): void {
    this.placePopoverAboveClosestModal();
    this.renderer.removeChild(
      this.elementRef.nativeElement.parentElement,
      this.elementRef.nativeElement
    );
  }

  ngOnDestroy(): void {
    this.hide();
  }

  private placePopoverAboveClosestModal() {
    const closestIonModal = this.elementRef.nativeElement.closest('ion-modal');
    if (closestIonModal) {
      this.zIndex = parseInt(closestIonModal.style.zIndex) + 1;
      this.elementRef.nativeElement.style.zIndex = `${this.zIndex}`;
    } else {
      this.zIndex = parseInt(DesignTokenHelper.zLayer('popover'));
    }
  }

  // document.removeEventListener needs the exact same event handler & options reference:
  private preventEvent(event: TouchEvent) {
    event.preventDefault();
  }

  private preventScroll() {
    this.isFirstToLockScroll = !this.document.body.classList.contains('backdrop-no-scroll');
    if (this.isFirstToLockScroll) {
      this.renderer.addClass(this.document.body, 'backdrop-no-scroll');
    }

    // preventDefault does not work with Renderer2.listen method; add event listener directly to document instead
    this.document.addEventListener(
      'touchmove',
      this.preventEvent,
      this.preventScrollEventListenerOptions
    );
  }

  private releaseScroll() {
    if (this.isFirstToLockScroll) {
      this.renderer.removeClass(this.document.body, 'backdrop-no-scroll');
    }

    this.document.removeEventListener(
      'touchmove',
      this.preventEvent,
      this.preventScrollEventListenerOptions
    );
  }

  show() {
    this.renderer.addClass(this.elementRef.nativeElement, 'is-opening');
    this.renderer.appendChild(this.document.body, this.elementRef.nativeElement);

    this.preventScroll();
    this.positionWrapper();

    this.renderer.addClass(this.elementRef.nativeElement, 'is-open');
    this.renderer.removeClass(this.elementRef.nativeElement, 'is-opening');

    this.isShowing = true;
  }

  hide() {
    this.renderer.removeChild(this.document.body, this.elementRef.nativeElement);
    this.releaseScroll();

    this.renderer.removeStyle(this.targetElement, 'z-index');
    this.renderer.removeStyle(this.targetElement, 'pointer-events');
    this.isShowing = false;
  }

  private positionWrapper() {
    const targetDimensions = this.targetElement.getBoundingClientRect();

    this.renderer.appendChild(this.document.body, this.elementRef.nativeElement);

    const wrapperElement = this.wrapperElement.nativeElement;
    const wrapperDimensions = wrapperElement.getBoundingClientRect();
    const viewport = wrapperElement.ownerDocument.defaultView;
    this.positionHorizontally(viewport, wrapperElement, targetDimensions, wrapperDimensions);
    this.positionVertically(viewport, wrapperElement, targetDimensions, wrapperDimensions);
  }

  private positionHorizontally(
    viewPort: Window,
    wrapperElement: HTMLDivElement,
    targetDimensions: DOMRect,
    wrapperDimensions: DOMRect
  ) {
    const viewPortWidth = viewPort.innerWidth;
    const contentWidth = wrapperDimensions.width;
    const availableSpaceRight = viewPortWidth - targetDimensions.left;
    const availableSpaceLeft = targetDimensions.right;
    const contentCanFitRightOfTarget =
      availableSpaceRight >= contentWidth + this.POPOVER_BODY_PADDING;
    const openRight = contentCanFitRightOfTarget || availableSpaceRight >= availableSpaceLeft;

    const [direction, oppositeDirection] =
      this.popout === HorizontalDirection.left || !openRight
        ? ['left', 'right']
        : ['right', 'left'];

    const pxValue =
      direction === 'left' ? viewPortWidth - targetDimensions.right : targetDimensions.left;

    this.renderer.removeStyle(wrapperElement, direction);
    this.renderer.setStyle(wrapperElement, oppositeDirection, `${pxValue}px`);
  }

  private positionVertically(
    viewPort: Window,
    wrapperElement: HTMLDivElement,
    targetDimensions: DOMRect,
    wrapperDimensions: DOMRect
  ) {
    const viewPortHeight = viewPort.innerHeight;
    const contentHeight = wrapperDimensions.height;
    const availableSpaceDown = viewPortHeight - targetDimensions.bottom;
    const availableSpaceUp = targetDimensions.top;
    const contentCanFitBelowTarget =
      availableSpaceDown >= contentHeight + this.POPOVER_BODY_PADDING;

    const isAvailableSpaceBelow =
      contentCanFitBelowTarget || availableSpaceDown >= availableSpaceUp;
    const [direction, oppositeDirection] = isAvailableSpaceBelow
      ? ['bottom', 'top']
      : ['top', 'bottom'];

    const pxValue =
      direction === 'bottom' ? targetDimensions.bottom : viewPortHeight - targetDimensions.top;

    this.renderer.removeStyle(wrapperElement, direction);
    this.renderer.setStyle(wrapperElement, oppositeDirection, `${pxValue}px`);

    if (direction === 'top') {
      // Ensure target is elevated above shadows in popover, i.e. content wrapped in Card:
      this.renderer.setStyle(this.targetElement, 'z-index', `${this.zIndex + 1}`);
      this.renderer.setStyle(this.targetElement, 'pointer-events', 'none');
    }
  }
}

